import React from "react";

import Card from "./Card";
import classes from "./ErrorModal.module.css";

const ErrorModal = (props) => {
  return (
    <Card>
      <div className={classes.overlay}>{props.message}</div>
    </Card>
  );
};

export default ErrorModal;
